import React, { Component } from 'react'
import GalleryItem from './GalleryItem';

export class Gallery extends Component {
    constructor(props){
        super(props)
        this.state = ""
    }

    //Function
    farmPhotos = (props) => {
        //generate image url with the provide template url
        let results = props.value;
        let obj;
        let objArr = [];

        //Iterate through the data
        results.map( function (result, i ) {
            // generate an array of URL for farming image that match the searched query 
            let jpg = `https://farm${result.farm}.staticflickr.com/${result.server}/${result.id}_${result.secret}.jpg`;
            let imgName = `${result.title}`;
            let key = i;


            obj = { jpg , imgName , key}

            objArr.push(obj);

            return objArr;
        });// end of ForEach
    }

     //Lifecycle hooks
    componenentDidMount(){
        this.farmPhotos()
    }

    render() {
        return (
            <div className="photo-container">
                <h2>Results</h2>
                <ul>
                    <GalleryItem obj={this.farmPhotos}/>
                </ul>
            </div>
        )
    }
}

export default Gallery
