import React, { Component } from 'react'
import axios from 'axios'
import './index.css'
import { BrowserRouter , Route , Switch } from 'react-router-dom'



//Import Components 
import apiKey from './components/Config'
import Search from './components/Search'
import Nav from './components/Nav'
import Gallery from './components/Gallery'
import NotFound from './components/NotFound'
import Loading from './components/Loading'


export class App extends Component {

  //Constructor of states
  constructor (){
    super()
    this.state = {
      button1 : [],
      button2 : [],
      button3 : [],
      search : [],
      query : 'stars',
      isLoading : true,
      error : null
    }
  }

  //Functions
  //Fetch data with Axios
  fetchPhotos = () => {
    let URL = `https://www.flickr.com/services/rest/?method=flickr.photos.search&text=${this.state.query}&per_page=24&api_key=${apiKey}&sort=relevance&safe_search=1&format=json&nojsoncallback=1`
    console.log(URL)
    axios.get(URL)
    .then(response => {
      // console.log(response.data.photos.photo)
      // console.log(this.state.query)
      //Check query and render accordingly
      if(this.state.query === 'sun'){
        this.setState({
          button1 : response.data.photos.photo, 
          isLoading : false
        })
        return this.state
      } 
      else if(this.state.query === 'stars') {

        this.setState({
          button2 : response.data.photos.photo,
          isLoading : false
        })

        return this.state
      }
      else if(this.state.query === 'moon') {

        this.setState({
          button3 : response.data.photos.photo,
          isLoading : false
        })

        return this.state
      }
      else {

        this.setState({
          search : response.data.photos.photo,
          isLoading : false
        })

        return this.state
      }
    })
    .catch(error => {
      console.log(error)
      this.setState({
        isLoading : true,
        error : true
      })
    })
  }//End of fetchPhotos()

  //Lifecycle hooks
  componentDidMount(){
    this.fetchPhotos();
   }




  render() {
    return (
      <BrowserRouter>
        <div className="container">
          <Search onSearch={this.fetchPhotos}/>
          <Nav />
          {
            (this.state.isLoading)
            ? <Loading/>
            :
            <Switch>
              <Route exact path="/"
              render={ () => <Gallery value={this.state.button1}/> } />
              <Route path="/sun"
              render={ () => <Gallery value={this.state.button1}/> } />
              <Route path="/stars"
              render={ () => <Gallery value={this.state.button2}/> } />
              <Route path="/moon"
              render={ () => <Gallery value={this.state.button3}/> } />
              <Route path="/search/:searchTerm"
              render={ () => <Gallery value={this.state.search}/> } />
              <Route component={NotFound} />
            </Switch>
          }
      </div>
      </BrowserRouter>


    )
  }
}

export default App
