import React, { Component } from 'react'
import axios from 'axios'
// import { BrowserRouter , Route } from 'react-router-dom'

//Import Components 
import apiKey from './components/Config'


export class App extends Component {

  //Constructor of states
  constructor (){
    super()
    this.state = {
      button1 : [],
      button2 : [],
      button3 : [],
      query : 'sun',
      isLoading : true,
      error : null
    }
  }

  //Functions
  processData = () => {

  }
  //Fetch data with Axios
  fetchPhotos = () => {
    let URL = `https://www.flickr.com/services/rest/?method=flickr.photos.search&text=${this.state.query}"&per_page=24&api_key=${apiKey}&sort="relevance"&safe_search="1"&format=json&nojsoncallback=1`
    axios.get(URL)
    .then(response => {
      console.log(response.data.photos.photo)

      if(this.state.query === 'sun'){
        this.setState(this.state.button1)

      }
    })
  }

  //Lifecycle hooks
  componenentDidMount(){
    this.fetchPhotos()
  }

  render() {
    return (
      <div className="container">
        <p>{this.fetchPhotos()}</p>
      </div>
    )
  }
}

export default App
