import React, { Component } from 'react'
import { withRouter } from 'react-router'

class Search extends Component {

    state = {
        searchText: ''
    }

  onSearchChange = e => {
    this.setState({ searchText: e.target.value })
  }

  handleSubmit = e => {
    //prevent default behaviour of page refresh
    e.preventDefault()
    let path = `/search/${this.query.value}`
    console.log( this.props)

    //push the query to path --ITWORKS
    this.props.history.push(path)

    //pass the search query back to app to fetch --DOESNTWORK
    // this.props.onSearch(this.state.searchText)
    console.log(this.props.onsearch())
    e.currentTarget.reset()
  }

  render() {  

    return (
      <form className="search-form" onSubmit={this.handleSubmit}>
        <input type="search" 
               onChange={this.onSearchChange}
               name="search" 
               ref={input => (this.query = input)}
               placeholder="Search..." />
        <button type="submit" className="search-button">
          <svg fill=" rgb(154, 155, 156)" height="24" viewBox="0 0 23 23" width="24" xmlns="http://www.w3.org/2000/svg">
            <path d="M15.5 14h-.79l-.28-.27C15.41 12.59 16 11.11 16 9.5 16 5.91 13.09 3 9.5 3S3 5.91 3 9.5 5.91 16 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z"/>
            <path d="M0 0h24v24H0z" fill="none"/>
          </svg>
        </button>
      </form>      
    )
  }
}

export default withRouter (Search)
