import React, { Component } from 'react'
import axios from 'axios'
import './index.css'
import { BrowserRouter , Route } from 'react-router-dom'

//Import Components 
import apiKey from './components/Config'
import Nav from './components/Nav'

export class App extends Component {

  //Constructor of states
  constructor (){
    super()
    this.state = {
      button1 : [],
      button2 : [],
      button3 : [],
      search : [],
      query : 'sun',
      isLoading : true,
      error : null
    }
  }

  //Functions
  processData = () => {

  }
  //Fetch data with Axios
  fetchPhotos = () => {
    let URL = `https://www.flickr.com/services/rest/?method=flickr.photos.search&text=${this.state.query}"&per_page=24&api_key=${apiKey}&sort="relevance"&safe_search="1"&format=json&nojsoncallback=1`
    axios.get(URL)
    .then(response => {
      //Check query and render accordingly
      if(this.state.query === 'sun'){
        this.setState({
          button1 : response.data.photos.photo, 
          isLoading : false
        })
      } 
      else if(this.state.query === 'stars') {
        this.setState({
          button2 : response.data.photos.photo,
          isLoading : false
        })
      }
      else if(this.state.query === 'moon') {
        this.setState({
          button3 : response.data.photos.photo,
          isLoading : false
        })
      }
      else {
        this.setState({
          search : response.data.photos.photo,
          isLoading : false
        })
      }
    })
    .catch(error => {
      console.log(error)
      this.setState({
        isLoading : false,
        error : true
      })
    })
  }//End of fetchPhotos()

  //Lifecycle hooks
  componenentDidMount(){
    this.fetchPhotos()
  }

  render() {
    return (
      <BrowserRouter>
        <div className="container">
          <Nav />
          <p>Hello World</p>
      </div>
      </BrowserRouter>
    )
  }
}

export default App
