import React from 'react';
import GalleryItem from './GalleryItem';
import Loading from './Loading';

const Gallery = ({saerch}) => {
  const {button1 , button2, button3, search} = props;
  //generate image url with the provide template url
  let results = search.value;
  let display;
  let obj;
  let objArr = [];

  console.log(results)

  //Iterate through the data
  results.map( function (result, i ) {
    // generate an array of URL for farming image that match the searched query 
    let jpg = `https://farm${result.farm}.staticflickr.com/${result.server}/${result.id}_${result.secret}.jpg`;
    let imgName = `${result.title}`;
    let key = i;


    obj = { jpg , imgName , key}

    objArr.push(obj);

    return objArr;
  }
  );// end of ForEach

  if (objArr.length > 0 ) {

    display = objArr.map((objItem, i) => {
      // console.log(objItem);
      display = <GalleryItem obj={objItem} key={objItem.key} />
      return display
    });//end of map
  } 
  else {
    display = <Loading/>
  }

  return (

      <div className="photo-container">
        <h2>Results</h2>
        <ul>
          {display}
        </ul>
      </div>
    );
}//end of component 

export default Gallery;
