import React, { Component } from 'react'
import axios from 'axios'
import './index.css'
import { BrowserRouter , Route } from 'react-router-dom'



//Import Components 
import apiKey from './components/Config'
import Header from './components/Header'
import Gallery from './components/Gallery'
import NotFound from './components/NotFound'
import Loading from './components/Loading'


export class App extends Component {

  //Constructor of states
  constructor (){
    super()
    this.state = {
      data : [],
      isLoading : true,
      error : null
    }
  }

  //Functions
  //Fetch data with Axios
  fetchPhotos = (query = "sun") => {
    let URL = `https://www.flickr.com/services/rest/?method=flickr.photos.search&text=${query}&per_page=24&api_key=${apiKey}&sort=relevance&safe_search=1&format=json&nojsoncallback=1`

    // console.log(query)
    axios.get(URL)
    .then(response => {
      this.setState({
        data : response.data.photos.photo,
        isLoading : false
      })
    })
    .catch(error => {
      console.log(error)
      this.setState({
        isLoading : true,
        error : true
      })
    })

    console.log(query)
  }//End of fetchPhotos()



  //Lifecycle hooks
  componentDidMount(){
    this.fetchPhotos()
  }//


  render() {
    const params = () => {
      switch(this.query) {

        case "":      
        return <Route exact path="/" render={ () => <Gallery title={'sun'} value={this.state.data}/> } />

        case "sun":   
        return <Gallery title={'sun'} value={this.state.data}/>

        case "stars": 
        return <Gallery title={'stars'} value={this.state.data}/>

        case "moon":  
        return <Gallery title={'moons'} value={this.state.data}/>

        case this.query : 
        return <Gallery title={this.query} value={this.state.data}/>

        default:      return <NotFound/>
      }
    }//

    return (
      <BrowserRouter>
        <div className="container">
          {/* <Header onSearch={this.fetchPhotos()}/> */}
          {
            (this.state.isLoading)
            ? <Loading/>
            :
            <ul>{ params() }</ul>
          }
      </div>
      </BrowserRouter>


    )
  }
}

export default App

